mapper.select:
select * from salary where salary_eno=#{userNo}

    and permit_time >=#{area.start}


    and permit_time <=#{area.end}

mapper.insert:
          INSERT INTO salary
          (
            salary_no,
            salary_eno,
            salary_basic,
            salary_performance,
            wage
            ,permit_time
	       )
	       VALUES
            (#{sno}
            ,#{eno}
            ,#{basic}
            ,#{performance}
            ,#{wage}
            ,#{permitTime}
	        );

mapper.update:
update salary
set salary_performance=#{performance},wage=#{wage}
where salary_no=#{sno}
and salary_eno=#{eno}
